import { decodePaymentRequest } from "@cashu/cashu-ts";
export function getPaymentRequestFromHeaders(headers, quite = false) {
    const header = headers.get("X-Cashu");
    if (!header) {
        if (!quite)
            throw new Error("Missing cashu header");
        else
            return undefined;
    }
    const request = decodePaymentRequest(header);
    // Clear the transport, since NUT-23 is only in-band payments
    request.transport = [];
    return request;
}
